for _ in range(int(input())):
	n = int(input())
	a = list(map(int, input().split()))
	pos = [0] * n
	for i in range(n):
		pos[a[i]-1] = i
	ans = ['0'] * n
	ans[0] = '1'
	l = pos[0]
	r = pos[n-1]
	for i in range(n):
		if(l == pos[i] and r == pos[i+1]):
			ans[i] = '1'
		elif(l < pos[i] and r > pos[i]):
			l = pos[i]
		elif(l > pos[i] and r < pos[i+1]):
			r = pos[i+1]
	print(''.join(ans))
